        private static int CalculateFloor(string Input)
        {
            var count = 0;
            foreach (var i in Input)
            {
                count = DetermineBracket(i, count);
            }
            return count;
        }

        private static int DetermineBracket(char i, int count)
        {
            if (i == '(')
            {
                count++;
            }
            else if (i == ')')
            {
                count--;
            }
            return count;
        }

        private static int CalculatePositionThatEntersBasement(string Input)
        {
            var count = 0;
            var basementCount = 0;
            var i = 0;
            while (count >= 0)
            {
                count = DetermineBracket(Input[i], count);
                basementCount++;
                i++;
            }
            return basementCount;
        }
